INSERT INTO `b_search_content_text` VALUES (1,'f10591b306cc35ed567604af57dcb373','ГЛАВНАЯ\r\n\r\n'),(2,'9d3f755e845d420ef4c36fe5ce9d31ca','ФОРУМ\r\n\r\n'),(3,'cfcc506d8d5bdf1bdc632e850e76c49c','МЕСТА ПРОВЕДЕНИЯ\r\n\r\n'),(4,'35ad64d2efe014c7fd70f2b330e9e15f','ПРЕПОДАВАТЕЛИ\r\n\r\n'),(6,'2d3740b6bf55bbd303b97a39a7c1c78b','БЛАГОТВОРИТЕЛЯМ\r\nБЛАГОТВОРИТЕЛЯМ...\r\n'),(7,'67ac224f3eee9aa88ea9484dcf6fd6fe','МАТЕРИАЛЫ\r\nМАТЕРИАЛЫ...\r\n'),(8,'df37ab08c8b1e167115df080b7cf0229','ДЛЯ РАЗРАБОТКИ\r\n\r\n'),(9,'a85d95f23f7bfe41efb8858847f5fbbb','СТАТЬИ\r\n\r\n'),(10,'217cb1ef199a2e9b6096377d9549e518','ФОТО\r\n\r\n'),(11,'319378127dd214f7c5a44e59e51848cb','ВИДЕО\r\n\r\n'),(12,'bc5c4b91d4a1b8f0dceb2635b23a4688','АУДИО\r\n\r\n'),(13,'29672224959661e25cfcc548ad1dc857','ЧЕЛОВЕК — ДИТЯ ПРИРОДЫ!!!\r\nВ ЭТОМ ГОДУ Я ОЧЕРЕДНОЙ РАЗ ПОСЕТИЛ ЙОГА-ЛАГЕРЬ АУРА, КОТОРЫЙ НАХОДИТСЯ НА СЕВЕРНОМ КАВКАЗЕ, НЕДАЛЕКО ОТ ГОРОДА ГЕЛЕНДЖИК. ЙОГА-ЛАГЕРЬ \"АУРА\" - ЭТО УНИКАЛЬНЫЙ БЛАГОТВОРИТЕЛЬНЫЙ ПРОЕКТ, СУЩЕСТВУЮЩИЙ ЗА СЧЁТ ПОЖЕРТВОВАНИЙ ОРГАНИЗАТОРОВ И УЧАСТНИКОВ.\r\n\rВ ЭТОМ ГОДУ Я ОЧЕРЕДНОЙ РАЗ ПОСЕТИЛ ЙОГА-ЛАГЕРЬ АУРА, КОТОРЫЙ НАХОДИТСЯ НА СЕВЕРНОМ КАВКАЗЕ, НЕДАЛЕКО ОТ ГОРОДА ГЕЛЕНДЖИК.\rЙОГА-ЛАГЕРЬ \"АУРА\" - ЭТО УНИКАЛЬНЫЙ БЛАГОТВОРИТЕЛЬНЫЙ ПРОЕКТ, СУЩЕСТВУЮЩИЙ ЗА СЧЁТ ПОЖЕРТВОВАНИЙ ОРГАНИЗАТОРОВ И УЧАСТНИКОВ. ПОДОБНОМУ ПРОЕКТУ НЕ СУЩЕСТВУЕТ АНАЛОГА ВО ВСЁМ МИРЕ.\rЛЮДИ ПРИЕЗЖАЮТ ТУДА ОТДОХНУТЬ, ПОЗНАКОМИТЬСЯ С ЕДИНОМЫШЛЕННИКАМИ, МОЖЕТ БЫТЬ И ПОВСТРЕЧАТЬ СВОЮ ДРУГУЮ ПОЛОВИНКУ, А ГЛАВНОЕ ПОЗАНИМАТЬСЯ ЙОГОЙ.\rЗАНЯТИЯ ПРОВОДЯТСЯ БЕСПЛАТНО. ПО ЗАВЕРШЕНИИ ОБУЧЕНИЯ УЧЕНИК БЛАГОДАРИТ УЧИТЕЛЯ ТАК, КАК СЧИТАЕТ ДОЛЖНЫМ. ЗДЕСЬ ОРГАНИЗАТОРЫ СТАРАЮТСЯ ВОЗРОДИТЬ ПРИНЦИП БЛАГОДАРЕНИЯ, КАК ЭТО БЫЛО ПРИНЯТО В ВЕДИЧЕСКОЙ КУЛЬТУРЕ: \"УЧИТЕЛЬ ПЕРЕДАЕТ ЗНАНИЕ  УЧЕНИК ЕГО УСВАИВАЕТ\" И БЛАГОДАРИТ ПРЕПОДАВАТЕЛЯ ТАК, КАК ОН СЧИТАЕТ НУЖНЫМ. ТАК ЖЕ СУЩЕСТВУЕТ ВОЗМОЖНОСТЬ ДЛЯ ПРЕПОДАВАТЕЛЕЙ ПОВЫСИТЬ СВОЙ ОПЫТ. МНОГИЕ ИМЕННО ЗДЕСЬ, В ПРЕДГОРЬЕ КАВКАЗА, НА ПРИРОДЕ, В НЕСКОЛЬКИХ ШАГАХ ОТ ДОЛЬМЕНОВ, В КОТОРЫХ ПРАКТИКОВАЛИ НАШИ ПРЕДКИ, ЗНАКОМЯТСЯ С ЙОГОЙ И ДЕЛАЮТ СВОИ ПЕРВЫЕ ШАГИ НА ЭТОМ ПУТИ.\rТОЛЬКО ЗДЕСЬ, ВЫРВАВШИСЬ НА НЕСКОЛЬКО ДНЕЙ ИЛИ НЕДЕЛЬ ИЗ СОЦИУМА, МОЖНО ПОЛУЧИТЬ ЭТОТ НЕЗАБЫВАЕМЫЙ ОПЫТ, КОТОРЫЙ МОЖЕТ ИЗМЕНИТЬ ВАШУ ЖИЗНЬ К ЛУЧШЕМУ.\rМОГУ ПРИЗНАТЬСЯ, ЧТО ПОСЛЕ ПЕРВОГО ПОСЕЩЕНИЯ ЙОГА ЛАГЕРЯ В ПРОШЛОМ ГОДУ, В ЭТОМ ГОДУ Я ДЕЛАЛ ВСЁ ВОЗМОЖНОЕ, ЧТОБЫ ОПЯТЬ СЮДА ПОПАСТЬ. КОГДА ПРИШЛОСЬ СМЕНИТЬ МЕСТО РАБОТЫ, Я БУДУЩЕМУ ШЕФУ СРАЗУ ПОСТАВИЛ УСЛОВИЕ, ЧТО УЕДУ В КОНЦЕ ЛЕТА НА ТРИ НЕДЕЛИ В РОССИЮ(В ГЕРМАНИИ НЕ ДАЮТ ОТПУСК, ЕСЛИ ЧЕЛОВЕК НЕ ОТРАБОТАЛ ПОЛ ГОДА, Я ЖЕ НАСТАИВАЛ НА ОТПУСК ЧЕРЕЗ ДВА МЕСЯЦА). ПО ИДЕЕ МЕНЯ БЫ ПО ТАКИМ УСЛОВИЯМ НА РАБОТУ БЫ И НЕ ВЗЯЛИ, НО Я ПОСТАВИЛ ЙОГУ ВЫШЕ ВСЕГО ОСТАЛЬНОГО, И ВСЁ ПОЛУЧИЛОСЬ БЛАГОПОЛУЧНО. МЫ ПРЕДПОЛАГАЕМ, А БОГИ РАСПОЛАГАЮТ.\rВ КОНЦЕ АВГУСТА Я УЖЕ БЫЛ В ПУТИ В СТРАНУ, К КОТОРОЙ ПИТАЮ ОСОБУЮ ПРИВЯЗАННОСТЬ, ХОТЯ КАК ГОВОРЯТ МУДРЫЕ ЛЮДИ: \"ПРИВЯЗАННОСТИ НИ К ЧЕМУ ХОРОШЕМУ НЕ ПРИВОДЯТ\". НО В ЭТОМ СЛУЧАЕ МЫ СДЕЛАЕМ ИСКЛЮЧЕНИЕ ;-). ТОТ, КТО ЗНАЕТ, О ЧЁМ Я ГОВОРЮ, НЕ СМЕНИТ РОССИЮ НИ НА КАКУЮ ДРУГУЮ СТРАНУ МИРА, КАКАЯ БЫ ОНА \"БЛАГОУСТРОЕННАЯ\" ИЛИ \"ЦИВИЛИЗОВАННАЯ\" ОНА НЕ БЫЛА (К СВЕДЕНИЮ СКАЖУ, ЧТО ЖИВУ УЖЕ 20 ЛЕТ В ГЕ РМАНИИ, УЕХАЛ В 93` В 11-ТИ ЛЕТНЕМ ВОЗРАСТЕ). АЛЬТРУИЗМ И САМООТДАЧА ПРЕПОДАВАТЕЛЕЙ И ОРГАНИЗАТОРОВ ЙОГА-ЛАГЕРЯ ЗАСЛУЖИВАЕТ УВАЖЕНИЯ. ДРУЖЕЛЮБИЕ И ГОСТЕПРИИМСТВО МЕСТНОГО НАСЕЛЕНИЯ И ЛЮДЕЙ, КОТОРЫЕ ПРИЕХАЛИ В ЙОГА ЛАГЕРЬ ГОВОРЯТ О ВЫСОКОМ УРОВНЕ НРАВСТВЕННОСТИ РУССКОГО НАРОДА. ЗДЕСЬ ЭГО ДАЛЕКО НЕ НА ПЕРВОМ МЕСТЕ, КАК ЭТО ЧАСТО ВСТРЕЧАЕТСЯ СРЕДИ ЛЮДЕЙ ЗАПАДНОГО СОЦИУМА. МОЖЕТ БЫТЬ И ПРАВЫ ТЕ ПРОРОКИ, КОТОРЫЕ УТВЕРЖДАЮТ ЧТО ВОЗРОЖДЕНИЕ ВЕДИЧЕСКОЙ КУЛЬТУРЫ ДА И БУДУЩЕЕ ВСЕЙ ПЛАНЕТЫ ЗА РОССИЕЙ. ИЛИ ТЕ КТО УТВЕРЖДАЕТ, ЧТО В БУДУЩЕМ ВЕСЬ МИР БУДЕТ СМОТРЕТЬ НА РОССИЮ, КАК НА ХОРОШИЙ ПРИМЕР, КАК НА СТРАНУ У ЗДРАВОМЫСЛЯЩЕГО НАСЕЛЕНИЯ, У КОТОРОЙ МОЖНО МНОГО ЧЕМУ НАУЧИТЬСЯ.\rКАК И В ПРОШЛЫЙ РАЗ, РЕБЯТА В КРАСНОДАРЕ МЕНЯ ДОБРОДУШНО ВСТРЕТИЛИ, ТАК, КАК БУДТО МЫ ДРУГ ДРУГА УЖЕ ЗНАЕМ ВСЮ ЖИЗНЬ, ПОМОГЛИ МНЕ СОРИЕНТИРОВАТЬСЯ И ОТПРАВИТЬСЯ ДАЛЬШЕ В АУРУ. КОГДА Я ПРИБЫЛ В ЙОГА-ЛАГЕРЬ, СОЗДАЛОСЬ ВПЕЧАТЛЕНИЕ, ЧТО Я НИКУДА И НЕ УЕЗЖАЛ. ПРОШЕДШИЙ ГОД КАЗАЛСЯ ОДНИМ ДНЁМ. Я ПОЧУВСТВОВАЛ БЛАГОПРИЯТНУЮ ЭНЕРГИЮ ЭТОГО МЕСТА ВСЕЙ СВОЕЙ СУЩНОСТЬЮ. ПЕРВЫМ ДЕЛОМ НЕВИДИМАЯ СИЛА ПОТЯНУЛА МЕНЯ К ДОЛЬМЕНАМ. ТАМ Я НЕСКОЛЬКО МИНУТ ПОСИДЕЛ, ПОДЫШАЛ, УСПОКОИЛСЯ ПОСЛЕ ДОЛГОЙ ДОРОГИ.\rСАМОЕ ИНТЕРЕСНОЕ ПРОИСХОДИЛО КОГДА Я ВОЗВРАЩАЛСЯ ДОМОЙ. ПОСЛЕ ВСЕХ \"НЕУДОБСТВ\": ОТСУТСТВИЯ ТЁПЛОЙ ВОДЫ, ОТСУТСТВИЯ САН.УЗЛА В СОСЕДНЕЙ КОМНАТЕ (ПРИХОДИЛОСЬ И ПОМЁРЗНУТЬ И КАК ГОВОРИТЬСЯ ПОМЕСИТЬ ГЛИНУ, ТАК КАК НА ПРОТЯЖЕНИИ ОДНОЙ НЕДЕЛИ ЛИЛ КАЖДЫЙ ДЕНЬ ДОЖДЬ), МНЕ НЕ ХОТЕЛОСЬ ВОЗВРАЩАЕТСЯ ИЗ СВОЕЙ ПАЛАТКИ НАЗАД В КВАРТИРУ. ЧУВСТВО СЖАТОСТИ И В КАКОЙ ТО СТЕПЕНИ ЗАКЛЮЧЁННОСТИ В СВОИХ ЧЕТЫРЁХ СТЕНАХ, ПРИ ВСЁМ КОМФОРТЕ, КОТОРЫЙ ПРЕДОСТАВЛЯЕТ ЖИЗНЬ В ГОРОДСКОЙ КВАРТИРЕ, ВОЗНИКАЛО ГДЕ-ТО ВНУТРИ. ЕСЛИ БЫЛ БЫ ВЫБОР, Я, НАВЕРНОЕ, БЫ ВЫБРАЛ ХОЛОДНУЮ НЕУДОБНУЮ ПАЛАТКУ НА СКЛОНЕ ГОРЫ, НО НА ПРИРОДЕ, НЕЖЕЛИ ТЁПЛУЮ УДОБНУЮ КРОВАТЬ В ГОРОДСКОМ БЕТОННОМ ЯЩИКЕ. ТЕПЕРЬ Я ОСОЗНАЛ, ЧТО ИМЕЕТСЯ ВВИДУ КОГДА ГОВОРИТЬСЯ: \"ЧЕЛОВЕК - ДИТЯ ПРИРОДЫ.\"\rМОГУ ВСЕМ ТОЛЬКО ПОЖЕЛАТЬ ПОЛУЧИТЬ ЭТОТ ЦЕННЫЙ ОПЫТ.ЙОГА-ЛАГЕРЬ \"АУРА\" ПОСЕЩАЮТ ЛЮДИ СО ВСЕГО МИРА. НИ КАКАЯ ДОРОГА НЕ ДАЛЕКА, НИ КАКОЙ БИЛЕТ НЕ ДОРОГ, НЕ КАКАЯ ПРЕГРАДА ЛИЧНОЙ ЖИЗНИ НЕ НЕПРЕОДОЛИМА, ЕСЛИ ТЫ ОТПРАВЛЯЕШЬСЯ В \"АУРУ\". НЕТ НИ ЧЕГО ВАЖНЕЙ ПРАКТИКИ ЙОГИ НА ПРИРОДЕ, ТАК КАК ИЗНАЧАЛЬНО ПРАКТИКОВАЛИ САДХАКИ ВСЕХ ВРЕМЁН.\rПРИСОЕДИНЯЙТЕСЬ, И ВЫ САМИ УБЕДИТЕСЬ В ЭТОМ.\rПРЕДЛАГАЕМ ВАМ ПОДДЕРЖАТЬ ЭТОТ УНИКАЛЬНЫЙ ПРОЕКТ, НАПРАВЛЕННЫЙ НА ОЗДОРОВЛЕНИЕ ОБЩЕСТВА. НАДЕЮСЬ, МЫ УВИДИМСЯ В ЙОГА-ЛАГЕРЕ АУРА В ГОДУ 2014.\rСЛАВА БОГАМ И БЛАГОДАРНОСТЬ ОРГАНИЗАТОРАМ И ПРЕПОДАВАТЕЛЯМ ЗА ИХ ТРУД!!!\rМИНЦ ЕВГЕНИЙ \rDEGGENDORF, 15.09.13\r\n'),(14,'714afd5cc7c0e7a1c7c4d55ac6a4638a','ЗА ЧЕРТОЙ\r\nЭТО ЧИСТОЕ МЕСТО - ЙОГА ЛАГЕРЬ АУРА. В ЙОГА ЛАГЕРЕ ПЕРЕД ВАМИ ОТКРЫВАЮТСЯ ШИРОКИЕ ВОЗМОЖНОСТИ. ЭТО ЧИСТОЕ МЕСТО - ЙОГА ЛАГЕРЬ АУРА. В ЙОГА ЛАГЕРЕ ПЕРЕД ВАМИ ОТКРЫВАЮТСЯ ШИРОКИЕ ВОЗМОЖНОСТИ. ЭТО ЧИСТОЕ МЕСТО - ЙОГА ЛАГЕРЬ АУРА. В ЙОГА ЛАГЕРЕ ПЕРЕД ВАМИ ОТКРЫВА